import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../styles/AddYacht.css'; // Crea este archivo CSS para estilizar el formulario

const AddYacht = () => {
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    pricePerHour: '',
    location: '',
    capacity: '',
    imageUrl: '' // Cambia esto si decides manejar imágenes de otra manera
  });
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/yachts', formData, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}` // Asegúrate de que el token esté almacenado en localStorage
        }
      });
      navigate('/yachts');
    } catch (error) {
      alert('Error al crear el yate');
    }
  };

  return (
    <div className="add-yacht-form">
      <h2>Agregar Nuevo Yate</h2>
      <form onSubmit={handleSubmit}>
        <label>Nombre:
          <input type="text" name="name" value={formData.name} onChange={handleChange} required />
        </label>
        <label>Descripción:
          <textarea name="description" value={formData.description} onChange={handleChange} required />
        </label>
        <label>Precio por Hora:
          <input type="number" name="pricePerHour" value={formData.pricePerHour} onChange={handleChange} required />
        </label>
        <label>Ubicación:
          <input type="text" name="location" value={formData.location} onChange={handleChange} required />
        </label>
        <label>Capacidad:
          <input type="number" name="capacity" value={formData.capacity} onChange={handleChange} required />
        </label>
        <label>URL de la Imagen:
          <input type="text" name="imageUrl" value={formData.imageUrl} onChange={handleChange} required />
        </label>
        <button type="submit">Agregar Yate</button>
      </form>
    </div>
  );
};

export default AddYacht;
