import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from '../components/AuthContext';
import axios from 'axios';

const Reservations = () => {
  const { user } = useContext(AuthContext);
  const [reservations, setReservations] = useState([]);
  const [yacht, setYacht] = useState('');
  const [date, setDate] = useState('');
  const [duration, setDuration] = useState('');
  const [totalPrice, setTotalPrice] = useState('');

  useEffect(() => {
    const fetchReservations = async () => {
      const response = await axios.get('/api/reservations');
      setReservations(response.data);
    };

    fetchReservations();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    await axios.post('/api/reservations', {
      yacht,
      user: user._id,
      date,
      duration,
      totalPrice,
    });
    setYacht('');
    setDate('');
    setDuration('');
    setTotalPrice('');
    // Refetch reservations after creating a new one
    const response = await axios.get('/api/reservations');
    setReservations(response.data);
  };

  return (
    <div>
      <h2>Reservas</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Yate:
          <input type="text" value={yacht} onChange={(e) => setYacht(e.target.value)} />
        </label>
        <label>
          Fecha:
          <input type="date" value={date} onChange={(e) => setDate(e.target.value)} />
        </label>
        <label>
          Duraci√≥n (horas):
          <input type="number" value={duration} onChange={(e) => setDuration(e.target.value)} />
        </label>
        <label>
          Precio Total:
          <input type="number" value={totalPrice} onChange={(e) => setTotalPrice(e.target.value)} />
        </label>
        <button type="submit">Reservar</button>
      </form>
      <ul>
        {reservations.map(reservation => (
          <li key={reservation._id}>
            {reservation.yacht} - {new Date(reservation.date).toLocaleDateString()} - {reservation.duration} horas - ${reservation.totalPrice}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Reservations;
